// Parse reminder URLs from stdout
const stdout = $input.first().json.stdout || '';
const urls = stdout.trim().split('\n').filter(url => url.trim().length > 0);

if (urls.length === 0) {
  return []; // No items to process
}

return urls.map(url => ({ json: { url: url.trim() } }));

// Parse the JSON output from Python script
const output = $input.first().json.stdout;
const originalUrl = $('Parse URLs').first().json.url;

// Handle potential extra output lines (library warnings)
const lines = output.trim().split('\n');
const jsonLine = lines[lines.length - 1];

try {
  const data = JSON.parse(jsonLine);
  data.source_url = originalUrl;
  return [{ json: data }];
} catch (e) {
  return [{ json: { error: 'Failed to parse JSON', raw: output, source_url: originalUrl } }];
}

// Parse Ollama response and format as markdown
const ollamaResponse = $input.first().json;
const videoData = $('Parse JSON').first().json;

let recipe;
try {
  recipe = JSON.parse(ollamaResponse.response);
} catch (e) {
  return [{ json: { error: 'Failed to parse recipe JSON', raw: ollamaResponse.response, source_url: videoData.source_url } }];
}

// Generate filename
const today = new Date().toISOString().split('T')[0];
const slug = (recipe.recipe_name || 'untitled-recipe')
  .toLowerCase()
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/^-|-$/g, '');
const filename = `${today}-${slug}.md`;

// Helper functions
const quoteOrNull = (val) => val ? `"${val}"` : 'null';
const numOrNull = (val) => val !== null && val !== undefined ? val : 'null';

// Format ingredients
const ingredients = (recipe.ingredients || []).map(ing => {
  const inferred = ing.inferred ? ' *(inferred)*' : '';
  return `- ${ing.quantity || ''} ${ing.item || ''}${inferred}`;
}).join('\n');

// Format instructions
const instructions = (recipe.instructions || []).map(inst => {
  const timeNote = inst.time ? ` (${inst.time})` : '';
  return `${inst.step || ''}. ${inst.text || ''}${timeNote}`;
}).join('\n');

// Format equipment list
const equipmentList = (recipe.equipment || []).map(e => `- ${e}`).join('\n');

// Format YAML arrays
const dietaryYaml = recipe.dietary && recipe.dietary.length > 0 
  ? `[${recipe.dietary.join(', ')}]` 
  : '[]';
const equipmentYaml = recipe.equipment && recipe.equipment.length > 0
  ? `[${recipe.equipment.map(e => `"${e}"`).join(', ')}]`
  : '[]';

// Format tags
const tags = [];
if (recipe.cuisine) tags.push(`  - ${recipe.cuisine.toLowerCase().replace(/ /g, '-')}`);
if (recipe.protein) tags.push(`  - ${recipe.protein.toLowerCase().replace(/ /g, '-')}`);
if (recipe.dish_type) tags.push(`  - ${recipe.dish_type.toLowerCase().replace(/ /g, '-')}`);
const tagsYaml = tags.length > 0 ? tags.join('\n') : '  - recipe';

// Build notes section
const notesParts = [];
if (recipe.storage) notesParts.push(`### Storage\n${recipe.storage}`);
if (recipe.variations && recipe.variations.length > 0) {
  const vars = recipe.variations.map(v => `- ${v}`).join('\n');
  notesParts.push(`### Variations\n${vars}`);
}
if (recipe.nutritional_info) notesParts.push(`### Nutritional Info\n${recipe.nutritional_info}`);
const notesSection = notesParts.length > 0 
  ? `\n\n## Notes\n\n${notesParts.join('\n\n')}\n` 
  : '';

// Build markdown
const markdown = `---
title: "${recipe.recipe_name || 'Untitled Recipe'}"
source_url: "${videoData.source_url || ''}"
source_channel: "${videoData.channel || 'Unknown'}"
date_added: ${today}
video_title: "${videoData.title || 'Unknown Video'}"

prep_time: ${quoteOrNull(recipe.prep_time)}
cook_time: ${quoteOrNull(recipe.cook_time)}
total_time: ${quoteOrNull(recipe.total_time || recipe.cook_time || recipe.prep_time)}
servings: ${numOrNull(recipe.servings)}
difficulty: ${quoteOrNull(recipe.difficulty)}

cuisine: ${quoteOrNull(recipe.cuisine)}
protein: ${quoteOrNull(recipe.protein)}
dish_type: ${quoteOrNull(recipe.dish_type)}
dietary: ${dietaryYaml}

equipment: ${equipmentYaml}

tags:
${tagsYaml}

needs_review: ${recipe.needs_review !== false ? 'true' : 'false'}
confidence_notes: "${recipe.confidence_notes || ''}"
---

# ${recipe.recipe_name || 'Untitled Recipe'}

> ${recipe.description || ''}

## Ingredients

${ingredients}

## Instructions

${instructions}

## Equipment

${equipmentList}
${notesSection}
---
*Extracted from [${videoData.title || 'Unknown Video'}](${videoData.source_url || ''}) on ${today}*
`;

return [{
  json: {
    filename,
    markdown,
    recipe_name: recipe.recipe_name,
    source_url: videoData.source_url
  }
}];
